  return $na;
}
//分頁導覽
function page($tb, $wh, $rg, $nw)
{
  $total = count(select($tb, $wh));
  $many = ceil($total / $rg);
  $pg[''] = ($nw == $many) ? $nw : $nw + 1;
  return $pg;
}
